import React, { Component } from "react";

export class App extends Component {
  state = {};

  // handle Change
  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  // handle Submit
  handleSubmit = e => {
    e.preventDefault();
    this.props.registerFlight(this.state);
  };
  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-6 m-auto mt-3 bg-danger text-white">
            <h1 className="text-center">Flights App</h1>
            <form className="form-group row m-3 " onSubmit={this.handleSubmit}>
              <label htmlFor="flightNumber">Fligh Number</label>
              <input
                type="text"
                name="flightNumber"
                className="form-control"
                onChange={this.handleChange}
              />
              <label htmlFor="destination">Destination</label>
              <input
                type="text"
                name="destination"
                className="form-control"
                onChange={this.handleChange}
              />
              <label htmlFor="departure">Departure</label>
              <input
                type="text"
                name="departure"
                className="form-control"
                onChange={this.handleChange}
              />
              <label htmlFor="date">Date</label>
              <input
                type="text"
                name="date"
                className="form-control"
                onChange={this.handleChange}
              />
              <button type="submit" className="btn btn-dark mt-3">
                Register
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
