const initialFlight = { flight: [] };
const registerFlightReducer = (state = initialFlight, action) => {
  switch (action.type) {
    case "REGISTER_FLIGHT":
      return (state = {
        flight: [
          ...state.flight,
          {
            flightNumber: action.flightInfo.flightNumber,
            destination: action.flightInfo.destination,
            departure: action.flightInfo.departure,
            date: action.flightInfo.date
          }
        ]
      });
    default:
      return state;
  }
};

export default registerFlightReducer;
