const deleteFlightReducer = (state, action) => {
  switch (action.type) {
    case "DELETE_FLIGHT":
      const flightTable = state.flight.fliter(flight => {
        return flight.flightNumber !== action.flightID;
      });
      return (state = {
        flight: [...flightTable]
      });
  }
};

export default deleteFlightReducer;
