import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import { Provider } from "react-redux";
import * as serviceWorker from "./serviceWorker";
import { createStore } from "redux";
import reducer from "./reducers/rootReducers";
import axios from "axios";
import {
  actionLoading,
  actionSuccess,
  actionFail
} from "./actions/rootActions";
import { createLogger } from "redux-logger";
import { applyMiddleware } from "redux";
// store
const logger = createLogger();

// dispatch register Flight

const getUsers = flightObject => {
  return dispatch => {
    // dispatch loading action
    store.dispatch(actionLoading());
    axios
      .get("https://randomuser.me/api/?results=10")
      .then(res => {
        // dispach success action
        dispatch(actionSuccess(res.data.results));
      })
      .catch(error => {
        // disptch error action
        dispatch(actionFail(error.message));
      });
  };
};
const store = createStore(reducer, applyMiddleware(logger));
store.dispatch(getUsers());
ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>,

  document.getElementById("root")
);

serviceWorker.unregister();
