export const registerFlight = flight => {
  return {
    type: "REGISTER_FLIGHT",
    flightInfo: {
      flightNumber: flight.flightNumber,
      destination: flight.destination,
      departure: flight.departure,
      data: flight.date
    }
  };
};

export const deleteFlight = flightID => {
  return {
    type: "DELETE_FLIGHT",
    flightInfo: {
      flightID: flightID
    }
  };
};
